export type DataStreamFieldType = "TEXT" | "NUMERIC" | "DATE" | "BOOLEAN" | "ENUM";
export interface DataStreamFieldMapSimple {
    type: DataStreamFieldType;
}
export interface DataStreamFieldMapComposite {
    type: "COMPOSITE";
    dataType: DataStreamFieldType;
    fields: string[];
}
export interface DataStreamFieldEnum<T extends string> {
    type: "ENUM";
    options: T;
}
export type DataStreamFieldMap = DataStreamFieldMapSimple | DataStreamFieldMapComposite;
