export interface DSBFieldValue<TValue> {
    operator: string;
    values: Array<TValue>;
}
export interface DSBField<TValue> {
    dataKeyIdx: number;
    compositeValues: boolean[];
    values: Array<DSBFieldValue<TValue>>;
}
export type DataKeyFieldType = "TEXT" | "NUMERIC" | "DATE" | "BOOLEAN";
export interface DataKeyCompositeField {
    name: string;
    displayName: string;
}
export interface DataStreamKey {
    name: string;
    displayName: string;
    description?: string;
    fieldType: DataKeyFieldType;
    types?: string[];
    compositeTypes?: DataKeyCompositeField[];
}
export interface DataStreamConfig {
    id: string;
    display: string;
    fields: DataStreamKey[];
}
