export type DataSubscriptionTarget = "WEBHOOK" | "WEBSOCKET";
export type DataStreamRuleType = "jsonlogic" | "accountfilter" | "unparsedtransaction" | "parsedaccountfilter";
export interface DataStreamAuthDetails {
    targetHeader?: string;
    targetValue: string;
}
export interface BaseSubscription {
    id?: string;
    subscriptionId?: string;
    name: string;
    dataStream: string;
    filter?: Record<string, any>[];
    deserializedFilter?: Record<string, any>[];
    dateCreated: Date;
    dateLastUpdated?: Date;
    ruleType?: DataStreamRuleType;
    authDetails?: DataStreamAuthDetails | null;
    apiKey?: string | null;
}
export interface WebhookTargetType {
    targetType: "WEBHOOK";
    targetValue: string;
}
export interface WebsocketTargetType {
    targetType: "WEBSOCKET";
}
export interface WebhookSubscription extends BaseSubscription, WebhookTargetType {
}
export interface WebsocketSubscription extends BaseSubscription, WebsocketTargetType {
}
export type Subscription = WebhookSubscription | WebsocketSubscription;
